"use client";

import * as React from "react";
import { useRouter } from "next/navigation";

import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Separator } from "@/components/ui/separator";
import { toast } from "@/components/ui/use-toast";
import { apiClient, extractErrorMessage } from "@/lib/api";
import { useAuthStore } from "@/lib/stores/useAuthStore";
import type { AuthResponse, LoginRequest } from "@/types/api";

export const LoginForm: React.FC = () => {
  const router = useRouter();
  const login = useAuthStore((state) => state.login);
  const [isSubmitting, startTransition] = React.useTransition();
  const [form, setForm] = React.useState<LoginRequest>({
    email: "",
    password: "",
    rememberMe: true,
  });

  const [error, setError] = React.useState<string | null>(null);

  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value, type, checked } = event.target;
    setForm((prev) => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const handleSubmit = (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    setError(null);

    if (!form.email || !form.password) {
      setError("Email and password are required.");
      return;
    }

    startTransition(async () => {
      try {
        const { data } = await apiClient.post<AuthResponse>("/auth/login", form);
        const payload = {
          user: data.data.user,
          tokens: data.data.tokens,
        };
        login(payload);
        toast({
          title: "Welcome back",
          description: "You have been signed in successfully.",
          variant: "success",
        });
        router.replace("/wallets");
      } catch (err) {
        const message = extractErrorMessage(err);
        setError(message);
      }
    });
  };

  return (
    <Card className="w-full max-w-md">
      <CardHeader>
        <CardTitle className="text-2xl">Sign in to your account</CardTitle>
        <p className="text-sm text-muted-foreground">
          Access your multi-chain wallets, track balances, and manage transactions.
        </p>
      </CardHeader>
      <CardContent>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div className="space-y-2">
            <Label htmlFor="email">Email</Label>
            <Input
              id="email"
              name="email"
              type="email"
              placeholder="you@example.com"
              value={form.email}
              autoComplete="email"
              onChange={handleChange}
            />
          </div>
          <div className="space-y-2">
            <Label htmlFor="password">Password</Label>
            <Input
              id="password"
              name="password"
              type="password"
              placeholder="••••••••••"
              value={form.password}
              autoComplete="current-password"
              onChange={handleChange}
            />
          </div>
          <div className="flex items-center justify-between text-sm">
            <label className="flex items-center gap-2">
              <input
                type="checkbox"
                name="rememberMe"
                checked={form.rememberMe}
                onChange={handleChange}
                className="h-4 w-4 rounded border-border"
              />
              <span>Remember me</span>
            </label>
            <a href="/forgot-password" className="text-primary hover:underline">
              Forgot password?
            </a>
          </div>
          {error && (
            <p className="rounded-md bg-destructive/10 px-3 py-2 text-sm text-destructive">
              {error}
            </p>
          )}
          <Button type="submit" className="w-full" disabled={isSubmitting}>
            {isSubmitting ? "Signing in..." : "Sign in"}
          </Button>
        </form>
        <Separator className="my-6" />
        <p className="text-center text-sm text-muted-foreground">
          New to Atlas Wallet?{" "}
          <a href="/register" className="text-primary hover:underline">
            Create an account
          </a>
        </p>
      </CardContent>
    </Card>
  );
};
EOF
"]}

