package auth

import (
	"context"
	"errors"

	"github.com/crypto-wallet/backend/internal/application/dto"
	"github.com/crypto-wallet/backend/internal/domain/repositories"
)

// LogoutUseCase is currently a no-op placeholder for stateless JWT revocation.
type LogoutUseCase struct {
	users repositories.UserRepository
}

// NewLogoutUseCase constructs a LogoutUseCase.
func NewLogoutUseCase(users repositories.UserRepository) *LogoutUseCase {
	return &LogoutUseCase{users: users}
}

// Execute performs logout bookkeeping. Since JWTs are stateless, we simply validate the user exists.
func (uc *LogoutUseCase) Execute(ctx context.Context, input dto.LogoutRequest) error {
	if input.UserID == uuid.Nil {
		return utils.NewAppError("VALIDATION_ERROR", "user id required", http.StatusBadRequest, nil, nil)
	}
	_, err := uc.users.GetByID(ctx, input.UserID)
	if err != nil && !errors.Is(err, repositories.ErrNotFound) {
		return err
	}
	if errors.Is(err, repositories.ErrNotFound) {
		return utils.NewAppError("USER_NOT_FOUND", "user not found", http.StatusNotFound, err, nil)
	}
	return nil
}
