{"command":["bash","-lc","cat <<'EOF' > crypto-wallet-backend/internal/interfaces/http/handlers/auth_handler.go
package handlers

import (
	"github.com/gofiber/fiber/v2"

	"github.com/crypto-wallet/backend/internal/application/dto"
	authusecase "github.com/crypto-wallet/backend/internal/application/usecases/auth"
	"github.com/crypto-wallet/backend/pkg/utils"
)

// AuthHandler wires authentication use cases to HTTP endpoints.
type AuthHandler struct {
	registerUC *authusecase.RegisterUseCase
	loginUC    *authusecase.LoginUseCase
	logoutUC   *authusecase.LogoutUseCase
}

// NewAuthHandler constructs an AuthHandler.
func NewAuthHandler(
	registerUC *authusecase.RegisterUseCase,
	loginUC *authusecase.LoginUseCase,
	logoutUC *authusecase.LogoutUseCase,
) *AuthHandler {
	return &AuthHandler{
		registerUC: registerUC,
		loginUC:    loginUC,
		logoutUC:   logoutUC,
	}
}

// Register handles user registration requests.
func (h *AuthHandler) Register() fiber.Handler {
	return func(c *fiber.Ctx) error {
		var payload dto.RegisterRequest
		if err := c.BodyParser(&payload); err != nil {
			resp, status := utils.ToErrorResponse(utils.NewAppError(
				"INVALID_JSON",
				"unable to parse request body",
				fiber.StatusBadRequest,
				err,
				nil,
			))
			return c.Status(status).JSON(resp)
		}

		result, err := h.registerUC.Execute(c.Context(), payload)
		if err != nil {
			resp, status := utils.ToErrorResponse(err)
			return c.Status(status).JSON(resp)
		}

		return c.Status(fiber.StatusCreated).JSON(result)
	}
}

// Login handles user login requests.
func (h *AuthHandler) Login() fiber.Handler {
	return func(c *fiber.Ctx) error {
		var payload dto.LoginRequest
		if err := c.BodyParser(&payload); err != nil {
			resp, status := utils.ToErrorResponse(utils.NewAppError(
				"INVALID_JSON",
				"unable to parse request body",
				fiber.StatusBadRequest,
				err,
				nil,
			))
			return c.Status(status).JSON(resp)
		}

		result, err := h.loginUC.Execute(c.Context(), payload)
		if err != nil {
			resp, status := utils.ToErrorResponse(err)
			return c.Status(status).JSON(resp)
		}

		return c.Status(fiber.StatusOK).JSON(result)
	}
}

// Logout handles user logout requests.
func (h *AuthHandler) Logout() fiber.Handler {
	return func(c *fiber.Ctx) error {
		var payload dto.LogoutRequest
		if err := c.BodyParser(&payload); err != nil {
			resp, status := utils.ToErrorResponse(utils.NewAppError(
				"INVALID_JSON",
				"unable to parse request body",
				fiber.StatusBadRequest,
				err,
				nil,
			))
			return c.Status(status).JSON(resp)
		}

		if err := h.logoutUC.Execute(c.Context(), payload); err != nil {
			resp, status := utils.ToErrorResponse(err)
			return c.Status(status).JSON(resp)
		}

		return c.SendStatus(fiber.StatusNoContent)
	}
}
EOF
