func (b *BaseAdapter) recordUnsigned(req *TransactionRequest, unsigned *UnsignedTransaction) {
	if unsigned == nil {
		return
	}
	state := &txState{
		unsigned:     unsigned,
		status:       TxStatusPending,
		amount:       req.Amount,
		fee:          req.Fee,
		fromAddress:  req.FromAddress,
		toAddress:    req.ToAddress,
		lastUpdated:  time.Now().UTC(),
	}
	b.txStore.Store(unsigned.TxHash, state)
}

func (b *BaseAdapter) recordSigned(hash string, signed *SignedTransaction) {
	if signed == nil {
		return
	}
	value, _ := b.txStore.Load(hash)
	if value == nil {
		value = &txState{status: TxStatusPending}
	}
	state, _ := value.(*txState)
	state.signed = signed
	state.lastUpdated = time.Now().UTC()
	b.txStore.Store(hash, state)
}

func (b *BaseAdapter) recordBroadcast(hash string) {
	value, _ := b.txStore.Load(hash)
	if value == nil {
		value = &txState{status: TxStatusPending}
	}
	state, _ := value.(*txState)
	state.status = TxStatusPending
	state.lastUpdated = time.Now().UTC()
	b.txStore.Store(hash, state)
}

func (b *BaseAdapter) getState(hash string) (*txState, bool) {
	if value, ok := b.txStore.Load(strings.TrimSpace(hash)); ok {
		if state, ok := value.(*txState); ok {
			b.progressState(state)
			return state, true
		}
	}
	return nil, false
}

func (b *BaseAdapter) progressState(state *txState) {
	if state == nil {
		return
	}
	now := time.Now().UTC()
	elapsed := now.Sub(state.lastUpdated)
	if state.status == TxStatusPending && elapsed > 3*time.Second {
		state.status = TxStatusPending
		state.lastUpdated = now
	}
	if state.status == TxStatusPending && elapsed > 6*time.Second {
		state.status = TxStatusConfirmed
		state.confirmations = b.confirmationThreshold
		if state.blockNumber == 0 {
			state.blockNumber = b.randomBlockNumber()
		}
		state.lastUpdated = now
	}
}

func (b *BaseAdapter) randomBlockNumber() uint64 {
	return uint64(time.Now().Unix()%100000) + uint64(rand.Intn(1000))
}

func (b *BaseAdapter) handleCreateTransaction(req *TransactionRequest) (*UnsignedTransaction, error) {
	if err := validateTransactionRequest(req); err != nil {
		return nil, err
	}
	metadata := mergeMetadata(req.Metadata)
	unsigned := &UnsignedTransaction{
		TxHash:   b.newHash(),
		RawTx:    []byte(time.Now().UTC().Format(time.RFC3339Nano)),
		Metadata: metadata,
	}
	b.recordUnsigned(req, unsigned)
	return unsigned, nil
}

func (b *BaseAdapter) handleSignTransaction(tx *UnsignedTransaction) (*SignedTransaction, error) {
	if tx == nil {
		return nil, errors.New("unsigned transaction required")
	}
	signed := &SignedTransaction{
		TxHash:   tx.TxHash,
		RawTx:    append([]byte{}, tx.RawTx...),
		Metadata: mergeMetadata(tx.Metadata, map[string]any{"signed": true}),
	}
	b.recordSigned(tx.TxHash, signed)
	return signed, nil
}

func (b *BaseAdapter) handleBroadcast(tx *SignedTransaction) (string, error) {
	if tx == nil {
		return "", errors.New("signed transaction required")
	}
	b.recordSigned(tx.TxHash, tx)
	b.recordBroadcast(tx.TxHash)
	return tx.TxHash, nil
}

func (b *BaseAdapter) handleGetTransaction(hash string) (*Transaction, error) {
	state, ok := b.getState(hash)
	if !ok {
		return nil, utils.NewAppError(
			"TRANSACTION_NOT_FOUND",
			"transaction not found",
			fiber.StatusNotFound,
			nil,
			nil,
		)
	}
	metadata := mergeMetadata(nil)
	if state.unsigned != nil {
		metadata = mergeMetadata(metadata, state.unsigned.Metadata)
	}
	if state.signed != nil {
		metadata = mergeMetadata(metadata, state.signed.Metadata)
	}
	return &Transaction{
		Hash:          strings.TrimSpace(hash),
		FromAddress:   state.fromAddress,
		ToAddress:     state.toAddress,
		Amount:        state.amount,
		Fee:           state.fee,
		BlockNumber:   state.blockNumber,
		Confirmations: state.confirmations,
		Status:        state.status,
		Metadata:      metadata,
		ErrorMessage:  state.errorMessage,
	}, nil
}

func (b *BaseAdapter) handleGetTransactionStatus(hash string) (*TransactionStatus, error) {
	state, ok := b.getState(hash)
	if !ok {
		return nil, utils.NewAppError(
			"TRANSACTION_NOT_FOUND",
			"transaction not found",
			fiber.StatusNotFound,
			nil,
			nil,
		)
	}
	return &TransactionStatus{
		TxHash:        strings.TrimSpace(hash),
		Status:        state.status,
		Confirmations: state.confirmations,
		BlockNumber:   state.blockNumber,
		ErrorMessage:  state.errorMessage,
	}, nil
}

func validateTransactionRequest(req *TransactionRequest) error {
	if req == nil {
		return errors.New("transaction request is required")
	}
	if strings.TrimSpace(req.FromAddress) == "" || strings.TrimSpace(req.ToAddress) == "" {
		return ErrInvalidAddress
	}
	if strings.TrimSpace(req.Amount) == "" {
		return errors.New("amount is required")
	}
	return nil
}

func mergeMetadata(values ...map[string]any) map[string]any {
	merged := map[string]any{}
	for _, value := range values {
		if value == nil {
			continue
		}
		for k, v := range value {
			merged[k] = v
		}
	}
	return merged
}

