    try {
      setLoading(true);
      const { data } = await apiClient.get<{ items: TransactionStatusResponse[] }>(
        "/transactions",
        {
          params: {
            walletId,
            chain,
          },
        },
      );
      setTransactions(data.items.map(mapTransaction));
      setError(null);
    } catch (err) {
      setError(extractErrorMessage(err));
    } finally {
      setLoading(false);
    }
  }, [walletId, chain]);

  const sendTransaction = useCallback(
    async (payload: Omit<SendTransactionRequest, "walletId"> & { walletId: string }) => {
      try {
        const { data } = await apiClient.post<TransactionStatusResponse> 
          ("/transactions", {
          ...payload,
        });
        const tx = mapTransaction(data);
        setTransactions((prev) => [tx, ...prev]);
        return tx;
      } catch (err) {
        throw new Error(extractErrorMessage(err));
      }
    },
    [],
  );

  const getTransactionById = useCallback(async (id: string) => {
    const { data } = await apiClient.get<TransactionStatusResponse>(`/transactions/${id}`);
    return mapTransaction(data);
  }, []);

  const getTransactionByHash = useCallback(
    async (hash: string, chainSymbol: string) => {
      const { data } = await apiClient.get<TransactionStatusResponse>(
        `/transactions/hash/${hash}`,
        {
          params: { chain: chainSymbol },
        },
      );
      return mapTransaction(data);
    },
    [],
  );

  useEffect(() => {
    fetchTransactions();
  }, [fetchTransactions]);

  useEffect(() => {
    if (!polling || !walletId) {
      return;
    }
    if (pollingRef.current) {
      clearInterval(pollingRef.current);
    }
    pollingRef.current = setInterval(() => {
      fetchTransactions();
    }, pollingInterval);

    return () => {
      if (pollingRef.current) {
        clearInterval(pollingRef.current);
      }
    };
  }, [polling, walletId, fetchTransactions, pollingInterval]);

  const latestTransaction = useMemo(() => transactions[0], [transactions]);

  return {
    transactions,
    latestTransaction,
    loading,
    error,
    setError,
    fetchTransactions,
    sendTransaction,
    getTransactionById,
    getTransactionByHash,
  };
};
