+"use client";
+
+import { create } from "zustand";
+
+import type { CreateWalletPayload, Wallet, WalletBalance, WalletListFilters } from "@/types/wallet";
+
+const API_BASE_URL = process.env.NEXT_PUBLIC_API_BASE_URL ?? "http://localhost:8080/api/v1";
+
+type WalletState = {
+  wallets: Wallet[];
+  total: number;
+  loading: boolean;
+  error?: string;
+  selectedWalletId?: string;
+  fetchWallets: (filters?: WalletListFilters) => Promise<void>;
+  createWallet: (payload: CreateWalletPayload) => Promise<Wallet | null>;
+  refreshWalletBalance: (walletId: string) => Promise<WalletBalance | null>;
+  setSelectedWallet: (walletId?: string) => void;
+};
+
+const mapWalletResponse = (payload: any): Wallet => ({
+  id: payload.id,
+  userId: payload.user_id,
+  chain: payload.chain,
+  address: payload.address,
+  label: payload.label,
+  balance: payload.balance,
+  balanceUsd: payload.balance_usd,
+  status: payload.status,
+  createdAt: payload.created_at,
+  updatedAt: payload.updated_at,
+  balanceUpdatedAt: payload.balance_updated_at,
+});
+
+const mapBalanceResponse = (payload: any): WalletBalance => ({
+  address: payload.address,
+  balance: payload.balance,
+  confirmations: payload.confirmations,
+  lastUpdated: payload.last_updated,
+});
+
+const buildParams = (filters?: WalletListFilters) => {
+  if (!filters) return "";
+  const params = new URLSearchParams();
+  if (filters.chain) params.set("chain", filters.chain);
+  if (filters.status) params.set("status", filters.status);
+  return params.toString();
+};
+
+const withAuthHeaders = () => {
+  const headers = new Headers({ "Content-Type": "application/json" });
+  if (typeof window !== "undefined") {
+    const token = window.localStorage.getItem("crypto-wallet-token");
+    if (token) headers.set("Authorization", `Bearer ${token}`);
+  }
+  return headers;
+};
+
+export const useWalletStore = create<WalletState>((set, get) => ({
+  wallets: [],
+  total: 0,
+  loading: false,
+  error: undefined,
+  selectedWalletId: undefined,
+  async fetchWallets(filters) {
+    set({ loading: true, error: undefined });
+    try {
+      const query = buildParams(filters);
+      const response = await fetch(
+        `${API_BASE_URL}/wallets${query ? `?${query}` : ""}`,
+        {
+          method: "GET",
+          headers: withAuthHeaders(),
+          credentials: "include",
+        },
+      );
+
+      if (!response.ok) {
+        const error = await response.json().catch(() => null);
+        throw new Error(error?.message ?? "Unable to load wallets");
+      }
+
+      const data = await response.json();
+      const wallets: Wallet[] = (data.wallets ?? data)?.map(mapWalletResponse) ?? [];
+      set({ wallets, total: data.total ?? wallets.length, loading: false, error: undefined });
+    } catch (err) {
+      set({ loading: false, error: err instanceof Error ? err.message : "Failed to load wallets" });
+    }
+  },
+  async createWallet(payload) {
+    set({ loading: true, error: undefined });
+    try {
+      const response = await fetch(`${API_BASE_URL}/wallets`, {
+        method: "POST",
+        headers: withAuthHeaders(),
+        credentials: "include",
+        body: JSON.stringify(payload),
+      });
+
+      if (!response.ok) {
+        const error = await response.json().catch(() => null);
+        throw new Error(error?.message ?? "Unable to create wallet");
+      }
+
+      const data = await response.json();
+      const wallet = mapWalletResponse(data);
+      set((state) => ({
+        wallets: [wallet, ...state.wallets],
+        total: state.total + 1,
+        loading: false,
+        selectedWalletId: wallet.id,
+      }));
+      return wallet;
+    } catch (err) {
+      set({ loading: false, error: err instanceof Error ? err.message : "Failed to create wallet" });
+      return null;
+    }
+  },
+  async refreshWalletBalance(walletId) {
+    try {
+      const response = await fetch(`${API_BASE_URL}/wallets/${walletId}/balance`, {
+        method: "GET",
+        headers: withAuthHeaders(),
+        credentials: "include",
+      });
+
+      if (!response.ok) {
+        const error = await response.json().catch(() => null);
+        throw new Error(error?.message ?? "Unable to refresh balance");
+      }
+
+      const data = await response.json();
+      const balance = mapBalanceResponse(data);
+      set((state) => ({
+        wallets: state.wallets.map((wallet) =>
+          wallet.id === walletId
+            ? {
+                ...wallet,
+                balance: balance.balance,
+                balanceUpdatedAt: balance.lastUpdated,
+              }
+            : wallet,
+        ),
+      }));
+      return balance;
+    } catch (err) {
+      set({ error: err instanceof Error ? err.message : "Failed to refresh balance" });
+      return null;
+    }
+  },
+  setSelectedWallet(walletId) {
+    set({ selectedWalletId: walletId });
+  },
+}));
