   112		}
   113		if generatedWallet == nil {
   114			return nil, fmt.Errorf("wallet service: blockchain adapter returned nil wallet")
   115		}
   116	
   117		privateKey := strings.TrimSpace(generatedWallet.PrivateKey)
   118		if privateKey == "" {
   119			return nil, fmt.Errorf("wallet service: blockchain adapter returned empty private key")
   120		}
   121	
   122		address := strings.TrimSpace(generatedWallet.Address)
   123		if address == "" {
   124			return nil, fmt.Errorf("wallet service: blockchain adapter returned empty address")
   125		}
   126	
   127		encryptedKey, err := s.encryptor.EncryptToString([]byte(privateKey), []byte(address))
