   245	// DecryptPrivateKey attempts to decrypt a previously stored private key using the configured encryptor.
   246	func (s *WalletService) DecryptPrivateKey(encrypted string, address string) (string, error) {
   247		if s.encryptor == nil {
   248			return "", ErrEncryptorNotConfigured
   249		}
   250		if strings.TrimSpace(encrypted) == "" {
   251			return "", fmt.Errorf("wallet service: encrypted payload is empty")
   252		}
   253		data, err := base64.StdEncoding.DecodeString(encrypted)
   254		if err != nil {
   255			return "", fmt.Errorf("wallet service: decode payload: %w", err)
   256		}
   257	
   258		// The encryptor interface only exposes EncryptToString. To support decrypting without
   259		// introducing a broader interface, we rely on type assertion where available.
   260		if decryptor, ok := s.encryptor.(interface {
   261			Decrypt([]byte, []byte) ([]byte, error)
   262		}); ok {
   263			plaintext, err := decryptor.Decrypt(data, []byte(address))
   264			if err != nil {
   265				return "", fmt.Errorf("wallet service: decrypt payload: %w", err)
   266			}
   267			return string(plaintext), nil
   268		}
   269	
   270		if decryptor, ok := s.encryptor.(interface {
   271			DecryptString(string, []byte) ([]byte, error)
   272		}); ok {
   273			plaintext, err := decryptor.DecryptString(encrypted, []byte(address))
   274			if err != nil {
   275				return "", fmt.Errorf("wallet service: decrypt payload: %w", err)
   276			}
   277			return string(plaintext), nil
   278		}
   279	
   280		return "", fmt.Errorf("wallet service: encryptor does not support decryption")
   281	}
