   245	func buildWalletHandler(cfg appConfig, pool *pgxpool.Pool, logger *slog.Logger) *handlers.WalletHandler {
   246		if pool == nil {
   247			return nil
   248		}
   249		if logger == nil {
   250			logger = slog.Default()
   251		}
   252	
   253		componentLogger := logging.WithComponent(logger, "wallet")
   254		key, err := resolveEncryptionKey(cfg.WalletEncryptionKey, componentLogger)
   255		if err != nil {
   256			componentLogger.Error("failed to resolve wallet encryption key", slog.String("error", err.Error()))
   257			return nil
   258		}
   259	
   260		encryptor, err := security.NewAESGCMEncryptor(security.AESGCMConfig{Key: key})
   261		if err != nil {
   262			componentLogger.Error("failed to initialise wallet encryptor", slog.String("error", err.Error()))
   263			return nil
   264		}
   265	
   266		walletRepo := postgres.NewWalletRepository(pool, logging.WithComponent(logger, "wallet-repository"))
   267	
   268		adapters := map[entities.Chain]blockchain.BlockchainAdapter{
   269			entities.ChainBTC: blockchain.NewBitcoinAdapter(cfg.Blockchain.Bitcoin, logging.WithComponent(logger, "blockchain-btc")),
   270			entities.ChainETH: blockchain.NewEthereumAdapter(cfg.Blockchain.Ethereum, logging.WithComponent(logger, "blockchain-eth")),
   271			entities.ChainSOL: blockchain.NewSolanaAdapter(cfg.Blockchain.Solana, logging.WithComponent(logger, "blockchain-sol")),
   272			entities.ChainXLM: blockchain.NewStellarAdapter(cfg.Blockchain.Stellar, logging.WithComponent(logger, "blockchain-xlm")),
   273		}
   274	
   275		service := services.NewWalletService(services.WalletServiceConfig{
   276			Repository: walletRepo,
   277			Encryptor:  encryptor,
   278			Adapters:   adapters,
   279			Logger:     logging.WithComponent(logger, "wallet-service"),
   280		})
   281	
   282		createUC := wallet.NewCreateWalletUseCase(service, logging.WithComponent(logger, "wallet-usecase-create"))
   283		listUC := wallet.NewListWalletsUseCase(service, logging.WithComponent(logger, "wallet-usecase-list"))
   284		balanceUC := wallet.NewGetWalletBalanceUseCase(service, logging.WithComponent(logger, "wallet-usecase-balance"))
   285	
   286		return handlers.NewWalletHandler(handlers.WalletHandlerConfig{
   287			CreateUseCase:  createUC,
   288			ListUseCase:    listUC,
   289			BalanceUseCase: balanceUC,
   290			Logger:         logging.WithComponent(logger, "wallet-handler"),
   291		})
   292	}
