   294	func resolveEncryptionKey(encoded string, logger *slog.Logger) ([]byte, error) {
   295		if logger == nil {
   296			logger = slog.Default()
   297		}
   298	
   299		trimmed := strings.TrimSpace(encoded)
   300		if trimmed == "" {
   301			logger.Warn("WALLET_ENCRYPTION_KEY not configured; generating ephemeral key")
   302			return security.GenerateRandomKey()
   303		}
   304	
   305		key, err := base64.StdEncoding.DecodeString(trimmed)
   306		if err != nil {
   307			logger.Warn("failed to decode WALLET_ENCRYPTION_KEY; generating ephemeral key", slog.String("error", err.Error()))
   308			return security.GenerateRandomKey()
   309		}
   310	
   311		if len(key) != security.AES256KeySize {
   312			logger.Warn("invalid WALLET_ENCRYPTION_KEY length; generating ephemeral key", slog.Int("length", len(key)))
   313			return security.GenerateRandomKey()
   314		}
   315	
   316		return key, nil
   317	}
