    57	func (h *WalletHandler) handleListWallets(c *fiber.Ctx) error {
    58		if h.listUseCase == nil {
    59			return fiber.NewError(fiber.StatusNotImplemented, "wallet listing not configured")
    60		}
    61	
    62		userID, err := h.extractUserID(c)
    63		if err != nil {
    64			return h.respondError(c, err)
    65		}
    66	
    67		limit := parseIntWithDefault(c.Query("limit"), 0)
    68		offset := parseIntWithDefault(c.Query("offset"), 0)
    69	
    70		input := usecasewallet.ListWalletsInput{
    71			UserID:    userID,
    72			Chain:     c.Query("chain"),
    73			Status:    c.Query("status"),
    74			Limit:     limit,
    75			Offset:    offset,
    76			SortBy:    c.Query("sort_by"),
    77			SortOrder: c.Query("sort_order"),
    78		}
    79	
    80		result, err := h.listUseCase.Execute(c.UserContext(), input)
    81		if err != nil {
    82			return h.respondError(c, err)
    83		}
    84	
    85		return c.Status(fiber.StatusOK).JSON(result)
    86	}
