   136	
   137	func (h *WalletHandler) extractUserID(c *fiber.Ctx) (string, error) {
   138		claims := c.Locals(middleware.AuthContextKey)
   139		if claims == nil {
   140			return "", fiber.NewError(fiber.StatusUnauthorized, "authentication required")
   141		}
   142	
   143		switch value := claims.(type) {
   144		case map[string]any:
   145			if id, ok := getString(value["user_id"]); ok {
   146				return id, nil
   147			}
   148			if id, ok := getString(value["sub"]); ok {
   149				return id, nil
   150			}
   151		case []any:
   152			for _, item := range value {
   153				if str, ok := getString(item); ok {
   154					return str, nil
   155				}
   156			}
   157		default:
   158			if str, ok := getString(value); ok {
   159				return str, nil
   160			}
   161		}
   162	
   163		return "", fiber.NewError(fiber.StatusUnauthorized, "user identifier missing from token")
   164	}
