   166	func getString(value any) (string, bool) {
   167		switch v := value.(type) {
   168		case string:
   169			if strings.TrimSpace(v) == "" {
   170				return "", false
   171			}
   172			return v, true
   173		default:
   174			return "", false
   175		}
   176	}
   177	
   178	func parseIntWithDefault(value string, fallback int) int {
   179		if strings.TrimSpace(value) == "" {
   180			return fallback
   181		}
   182		parsed, err := strconv.Atoi(value)
   183		if err != nil {
   184			return fallback
   185		}
   186		return parsed
   187	}
